mapper.update:
UPDATE product
prod_name = #{prodName} , price = #{price}, prod_detail = #{prodDetail} ,
WHERE prod_No = #{prodNo}

mapper.select:
  	SELECT *
  	FROM (	
  	SELECT inner_table.* , ROWNUM AS row_seq
  	FROM (	
  		SELECT prod_no , prod_detail , price, prod_name , reg_date, IMAGE_FILE
		FROM product



		 prod_no = CAST(#{searchKeyword} AS INTEGER)


			prod_name = #{searchKeyword}



		ORDER BY prod_no 
		) inner_table
		WHERE ROWNUM <= #{endRowNum} 
		)
		WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT prod_no , prod_detail , price, prod_name , reg_date
					FROM product



					 			 prod_no = CAST(#{searchKeyword} AS INTEGER)


					 			prod_name = #{searchKeyword}


					 ) countTable
